import React from 'react'
import {withFormik} from "formik";
import {checkOnMax, checkOnMin, checkOnRequired} from "../../../store/utils/form_helpers";
import OrderForm from "./Form";
import {withSnackbar} from "notistack";
import {withRouter} from "react-router-dom";

const getDefaultValues = ({typeVariant, ph, ...state}) => {
    let values = {
        typeVariant: typeVariant === 1 ? '' : typeVariant === 2 ? "long" : "short",
    }

    // inputs
    if (Object.keys(ph).length > 0) {
        values.ph = {}
        Object.keys(ph).forEach(keyName => {
            values.ph[keyName] = ""
        })
    }
    return values
}

const EnhancedForm = withFormik({
    mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

    // Custom sync validation
    validate: (values, {orderFormData: {ph, typeVariant}, ...props}) => {
        const errors = {};
        const rules = {}

        Object.keys(ph).forEach(keyName => {
            rules[keyName] = ph[keyName].rules
        })

        if (typeVariant === 1 && !values.typeVariant) errors["typeVariant"] = "Required"

        Object.keys(rules).forEach(keyName => {
            if (rules[keyName].hasOwnProperty('required')) {
                if (rules[keyName].required === true && !checkOnRequired(values.ph[keyName])) {
                    if (!errors.ph) errors.ph = {}
                    errors.ph[keyName] = 'Обязательно к заполнению'
                }
            }
            if (rules[keyName].hasOwnProperty('min')) {
                let limit = rules[keyName].min
                if (!checkOnMin(values.ph[keyName], limit)) {
                    if (!errors.ph) errors.ph = {}
                    errors.ph[keyName] = `Минимальная длина ${limit} символов`
                }
            }
            if (rules[keyName].hasOwnProperty('max')) {
                let limit = rules[keyName].max
                if (!checkOnMax(values.ph[keyName], limit)) {
                    if (!errors.ph) errors.ph = {}
                    errors.ph[keyName] = `Максимальная длина ${limit} символов`
                }
            }
        })

        return errors;
    },

    handleSubmit: (values, {setSubmitting, ...props}) => {
        const {makeOrderRequest, enqueueSnackbar, history} = props.props

        const data = {
            ...values
        }

        setSubmitting(true);
        makeOrderRequest(data).then(
            res => {
                // success
                setSubmitting(false);
                console.log(res)
                // history.push(`/compositions`)
                // разделить пополам редьюсер и скомбайнить
                // сделать крутилку под isPosting

                enqueueSnackbar('Заявка на рисунок успешно оформлена', {
                    variant: 'success',
                    autoHideDuration: 5000,
                });
            },
            err => {
                // error
                setSubmitting(false);
                console.log(err.message)
                debugger
                enqueueSnackbar('Упс! Произошла ошибка', {
                    variant: 'error',
                    autoHideDuration: 5000,
                });
            }
        )
    },

    enableReinitialize: true,

    displayName: 'OrderForm',
})(OrderForm)

export default withSnackbar(withRouter(EnhancedForm))
