import React from 'react';
import Container from "@material-ui/core/Container";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import {makeStyles} from "@material-ui/core/styles";
import {useStyles} from "../_layouts/default/LayoutUseStyles";
import Paper from "@material-ui/core/Paper";
import auth from '../../../auth'

const useLocalClasses = makeStyles(theme => ({
    papers: {
        display: 'flex',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(16),
            height: theme.spacing(16),
        },
    },
}));

export default function Home(props) {
    const classes = useStyles();
    const cls = useLocalClasses();

    return (
        <div className={classes.root}>

            <div className={classes.wrapper}>
                <div className={classes.content}>
                    <div>
                        <h1>Предложи свою идею для создания композиции или зацени уже имеющиеся!</h1>
                    </div>
                    <div>
                        <button onClick={() => {
                            auth.login(() => {
                                props.history.push("/compositions")
                            })
                        }}>Login
                        </button>
                    </div>
                    <Divider variant="middle"/>
                    <main>
                        <div className={cls.papers}>
                            <Paper elevation={3}></Paper>
                            <Paper elevation={3}></Paper>
                        </div>
                    </main>
                </div>
                <footer className={classes.footer}>
                    <Container maxWidth="sm">
                        <Box display="flex" justifyContent="center" alignItems="center">
                            <Typography variant="body1"><q>Продай разум и купи изумление, так как проницательность ума
                                является мыслью, а изумление — истинным видением.</q>&ndash;
                                <cite>Руми</cite>.
                            </Typography>
                        </Box>
                    </Container>
                    <Container maxWidth="sm">
                        <Box display="flex" justifyContent="center" alignItems="center">
                            <Copyright/>
                        </Box>
                    </Container>
                </footer>
            </div>
        </div>
    );
}
