import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Typography from "@material-ui/core/Typography";
import Toolbar from '@material-ui/core/Toolbar';
import {connect} from "react-redux";
import {login, logout} from "../../../../store/actions/AuthActions";
import styled from 'styled-components'

import Logo from "./Logo";
import LoginButton from "./LoginButton";
import NavProfile from "./NavProfile";

const StyledAppBar = styled(AppBar)`
    z-index: ${({theme}) => theme.zIndex.drawer + 1};
`


class SteamAuthButton extends React.Component {
    onHandleLogin() {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/auth/steam/handle', '_blank', 'width=800, height=600');
        if (window.focus) popupWindow.focus();
    }

    componentDidMount() {
        window.addEventListener('message', event => {
            if (event.origin !== process.env.REACT_APP_API_URL) return;

            const { token, ok } = event.data;

            if (ok) {
                localStorage.setItem('authToken', token);
                console.log(token);
            }
        });
    }

    render() {
        return (
            <div>
                <img
                    onClick={this.onHandleLogin}
                    src="https://steamcommunity-a.akamaihd.net/public/images/signinthroughsteam/sits_01.png"
                    alt="Login with Steam"
                />
            </div>
        );
    }
}


function Header({userInfo, userActions, ...props}) {


    const ProfileCtx = (userInfo.isAuthenticated ?
            <NavProfile avatar={userInfo.avatar} username={userInfo.username} logout={userActions.logout}/> :
            <SteamAuthButton />
    )
    // <LoginButton login={userActions.login}/>

    return (
        <StyledAppBar position="fixed" color="default">
            <Toolbar disableGutters style={{padding: '0 4px',}}>
                <Typography style={{flexGrow: 1,}}>
                    <Logo/>
                </Typography>
                {ProfileCtx}
            </Toolbar>
        </StyledAppBar>
    );
}

const mapStateToProps = (store) => ({
    userInfo: store.user
})
const mapDispatchToProps = (dispatch) => ({
    userActions: {
        login: () => dispatch(login()),
        logout: () => dispatch(logout()),
    }
})

export default connect(mapStateToProps, mapDispatchToProps)(Header)
