import React from 'react';
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import useMediaQuery from "@material-ui/core/useMediaQuery/useMediaQuery";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import {ThemeProvider} from '@material-ui/core/styles';
import {ProtectedRoute} from "./ProtectedRoute";

import Home from "./pages/home/Home";
import Compositions from "./pages/compositions/Compositions";
import NotFound from "./pages/error/NotFound";
import Suggest from "./pages/suggest/Suggest";

function App() {
    const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');

    const theme = React.useMemo(
        () =>
            createMuiTheme({
                palette: {
                    type: prefersDarkMode ? 'light' : 'dark',
                    primary: {
                        main: '#3f51b5',
                        mainGradient: prefersDarkMode ?
                            "linear-gradient(0, rgb(238, 238, 238) 20%, rgb(46, 204, 113, .5) 50%, rgb(238, 238, 238) 100%)" :
                            "linear-gradient(0, rgb(66, 66, 66) 20%, rgb(155, 89, 182, .5) 50%, rgb(66, 66, 66) 100%)",
                    },
                },
            }),
        [prefersDarkMode],
    );

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline/>
            <Router>
                <Switch>
                    <Route exact path="/" component={Home}/>
                    <Route path="/compositions" component={Compositions}/>
                    <Route path="/suggest" component={Suggest}/>
                    <Route path="*" component={NotFound}/>
                </Switch>
            </Router>
        </ThemeProvider>
    );
}

export default App
