import React, {Component} from 'react'
import Header from "../_layouts/_includes/header/Header";
import Footer from "../_layouts/_includes/footer/Footer";
import {withTheme} from '@material-ui/core/styles';
import styled from 'styled-components'

const Toolbar = styled.div`
    ${({theme}) => `
        min-height: ${theme.mixins.toolbar.minHeight}px;
    `}
`

const SteamBackground = styled.div`
    min-height: 100%;
    height: auto;
    position: relative;
    min-width: 950px;
    background-color: #000000;
    background-position: center top;
    background-repeat: no-repeat;
    background-image: url('https://steamcdn-a.akamaihd.net/steamcommunity/public/images/items/282760/4ee088bd6fdce882ea787242da79e01dfb150d77.jpg');
`

const ProfileHeaderBg = styled.div`
    position: relative;
    max-width: 1018px;
    min-height: 224px;
    padding-bottom: 16px;
    margin: 0 auto;
    background-repeat: no-repeat;
    background-position: center bottom;
`

const ProfileHeaderBgTexture = styled.div`
    max-width: 976px;
    min-height: 224px;
    margin: 0 auto;
    background-image: url('https://steamcommunity-a.akamaihd.net/public/images/profile/profile_header_bg_texture.jpg');
    background-repeat: no-repeat;
    background-position: center;
    box-shadow: 0 0 11px 2px black;
`

const ProfileHeader = styled.div`
    position: relative;
    max-width: 926px;
    margin: 0 auto;
    padding-top: 24px;
`
const ProfileHeaderContent = styled.div`
    position: relative;
    padding-top: 8px;
    color: #898989;
    font-size: 13px;
`

const ProfileHeaderCenteredPersona = styled.div`
    position: absolute;
    left: 186px;
    right: 268px;
    white-space: nowrap;
`

const PersonaName = styled.div`
    line-height: 40px;
    font-size: 24px;
    color: #ffffff;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    font-weight: 200;
`

const PlayerAvatarWrapper = styled.div`
    border-radius: 0;
    padding: 1px;
    background-color: #57cbde;
    background: linear-gradient( to bottom, rgba(83,164,196,1) 5%, rgba(69,128,151,1) 95%);
    filter: none;
    position: relative;
    width: 166px;
    height: 166px;
    float: left;
    margin-right: 18px;
`

const PlayerAvatarInner = styled.div`
    position: absolute;
    top: 2px;
    right: 2px;
    bottom: 2px;
    left: 2px;
`

const PlayerAvatar = styled.img`
    background: none;
    filter: none;
    display: block;
    padding: 0;
    width: 100%;
    height: 100%;
`

const ProfileHeaderSummary = styled.div`
    overflow: hidden;
    padding-right: 18px;
`

const ProfileContent = styled.div`
    position: relative;
    padding: 0 12px 64px 12px;
    width: 926px;
    margin: 0 auto;
    font-size: 13px;
    background: rgba(23, 23, 23, 0.85);
    margin-top: -16px;
    overflow: hidden;
    padding-top: 16px;
`

const ProfileContentInner = styled.div`
    position: relative;
`

const ProfileRightCol = styled.div`
    width: 268px;
    float: right;
`

const ProfileLeftCol = styled.div`
    width: 636px;
    float: left;
`

const ProfileCustomizationArea = styled.div`
`

const ProfileCustomization = styled.div`
    position: relative;
    background-image: url('https://steamcommunity-a.akamaihd.net/public/images/profile/showcase_bg.png');
    background-position: top left;
    background-repeat: no-repeat;
    background-color: #141414;
    border-radius: 3px;
    padding: 0 10px 11px 10px;
    margin-bottom: 12px;
    font-size: 13px;
    overflow: hidden;
`

const ProfileCustomizationHeader = styled.div`
    font-weight: 200;
    color: #5491cf;
    font-size: 20px;
    line-height: 30px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
`

const ProfileCustomizationBlock = styled.div`
`

const ScreenshotShowcase = styled.div`
`

const ScreenshotShowcasePrimary = styled.div`
    display: block;
    float: left;
    position: relative;
    width: 508px;
    max-width: 83%;
`

const ScreenshotShowcaseRightCol = styled.div`
    float: right;
    max-width: 17%;
    width: 102px;
    text-align: center;
`

const ScreenshotShowcasePrimaryLink = styled.a`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    margin-bottom: 4px;
`

const ScreenshotShowcasePrimaryImage = styled.img`
    display: block;
    max-width: 506px;
`

const ScreenshotShowcaseSmall = styled.div`
    display: block;
    float: left;
    position: relative;
    float: none;
    margin-bottom: 11px;
`
const ScreenshotShowcaseSmallImage = styled.img`
    display: block;
`

const ScreenshotShowcaseSmallLink = styled.a`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    margin-bottom: 4px;
`


class ShowcaseContainer extends Component {

    constructor(props) {
        super(props)
        this.state = {}
    }

    render() {
        return (
            <>
                <Header/>
                <Toolbar theme={this.props.theme}/>


                <SteamBackground>
                    <ProfileHeaderBg>
                        <ProfileHeaderBgTexture>
                            <ProfileHeader>
                                <ProfileHeaderContent>
                                    <ProfileHeaderCenteredPersona>
                                        <PersonaName>
                                            <span>Nacho</span>
                                        </PersonaName>
                                    </ProfileHeaderCenteredPersona>
                                    <PlayerAvatarWrapper>
                                        <PlayerAvatarInner>
                                            <PlayerAvatar/>
                                        </PlayerAvatarInner>
                                    </PlayerAvatarWrapper>
                                    <ProfileHeaderSummary>
                                        <PersonaName>
                                            <span>&nbsp;</span>
                                        </PersonaName>
                                    </ProfileHeaderSummary>
                                </ProfileHeaderContent>
                            </ProfileHeader>
                        </ProfileHeaderBgTexture>
                    </ProfileHeaderBg>
                    <ProfileContent>
                        <ProfileContentInner>
                            <ProfileRightCol/>
                            <ProfileLeftCol>
                                <ProfileCustomizationArea>
                                    <ProfileCustomization>
                                        <ProfileCustomizationHeader>Витрина иллюстраций</ProfileCustomizationHeader>


                                        <ProfileCustomizationBlock>
                                            <ScreenshotShowcase>
                                                <ScreenshotShowcasePrimary>
                                                    <ScreenshotShowcasePrimaryLink href="https://steamcommunity.com/sharedfiles/filedetails/?id=1972949040"
                                                                                   data-appid="767" data-publishedfileid="1972949040">
                                                        <ScreenshotShowcasePrimaryImage width="100%" src="./Сообщество Steam __ Nacho_files/1579411093_Comp_9.gif"/>
                                                    </ScreenshotShowcasePrimaryLink>
                                                </ScreenshotShowcasePrimary>
                                                <ScreenshotShowcaseRightCol>
                                                    <ScreenshotShowcaseSmall>
                                                        <a className="screenshot_showcase_screenshot modalContentLink ugc"
                                                           href="https://steamcommunity.com/sharedfiles/filedetails/?id=1972948662"
                                                           data-appid="767" data-publishedfileid="1972948662">
                                                            <img width="100%" style={{maxWidth: 100,}}
                                                                 src="./Сообщество Steam __ Nacho_files/saved_resource"/>
                                                        </a>
                                                    </ScreenshotShowcaseSmall>
                                                </ScreenshotShowcaseRightCol>
                                                <div style={{clear: 'both',}}/>
                                            </ScreenshotShowcase>
                                        </ProfileCustomizationBlock>


                                    </ProfileCustomization>
                                </ProfileCustomizationArea>
                            </ProfileLeftCol>
                            <div style={{clear: 'both',}}/>
                        </ProfileContentInner>
                    </ProfileContent>

                </SteamBackground>

                <Footer/>
            </>
        )
    }
}

export default withTheme(ShowcaseContainer)
