import {makeStyles} from "@material-ui/core/styles"

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
        backgroundColor: '#ddd',
        margin: '0 auto 25px',
    },
    control: {
        padding: theme.spacing(2),
    },


    // pagination
    pagination: {
        boxSizing: 'border-box',
        lineHeight: 1,
        textAlign: 'center',
        transition: 'font-size .2s ease-in-out',
        fontSize: '12pt',

        listStyleType: 'none',
        display: 'inline',
        margin: 0,
        padding: '.5em',
    },

    paginationLink: {
        cursor: 'pointer',
    },
    // pageClassName
    side: {
        display: 'inline-block',
        fontSize: '100%',
        width: 'auto',
        borderRadius: 3,
    },
    sideLink: {
        padding: '.5em',
    },

    item: {
        display: 'inline-block',
        fontSize: '100%',
        width: 'auto',
        borderRadius: 3,
    },

    itemLink: {
        color: '#777',
        fontSize: '100%',
        padding: '.5em',

        '&:focus, &:hover': {
            color: '#f60',
        },
    },

    itemActive: {
        background: 'rgba(0,0,0,.1)',
    },

    itemDisabled: {
        // cursor: 'default',
        opacity: .5,
        pointerEvents: 'none',
    },
}))

export default useStyles
