import React, {Component} from 'react'
import Typography from "@material-ui/core/Typography";
import {withRouter} from "react-router-dom";
import CompositionList from "./CompositionList";
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";
import {fetchList} from "../../../store/ducks/compositions/actions";

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`

class CompositionsContainer extends Component {

    constructor(props) {
        super(props)
        this.state = {
            compositions: props.compositions,
        }
    }

    componentDidMount() {
        if ( this.props.compositions.length === 0 ) {
            this.props.fetchList()
        }
    }

    render() {
        return (
            <SecondaryLayout>
                <PageCaptionSpacer>
                    <Container maxWidth="xl">
                        <Typography component="h1" variant="h5" align="left" color="textPrimary">
                            Листинг композиций
                        </Typography>
                    </Container>
                </PageCaptionSpacer>

                <CompositionList compositions={this.state.compositions}/>
            </SecondaryLayout>
        )
    }
}

const mapStateToProps = (store) => ({
    compositions: store.compositions.compositions
})

export default connect(mapStateToProps, {fetchList})(withRouter(CompositionsContainer))
