import axios from 'axios'
// import store from '../store'
import {logout} from "../ducks/auth/operations";

axios.defaults.headers.common["X-Requested-With"] = "XMLHttpRequest";
axios.defaults.withCredentials = true;
axios.defaults.baseURL = process.env.REACT_APP_API;

const fetch = (url, method, body) => {

    const options = {
        method,
        headers: requestHeaders(),
    };
    // console.log(body)

    if (method === "POST") {
        return axios.post(url, JSON.stringify(body), options)
            .then(res => parseStatus(res.status, res.data))
            .catch(err => parseStatus(err.response.status, err.response.data))
    }
    if (method === "GET") {
        return axios.get(url, options)
            .then(res => parseStatus(res.status, res.data))
            .catch( async (err) => {
                const res = await parseStatus(err.response.status, err.response.data)
                console.log(res)
                return res
                // if (status === 401) {
                //     debugger
                //     logout()
                // }
            })
    }
};

export default fetch

function parseStatus(status, res) {
    return new Promise( (resolve, reject) => {
        if (status >= 200 && status < 300) {
            resolve(res);
        } else {
            reject({status, message: res.message});
        }
    });
}

function requestHeaders() {
    const headers = {
        "Accept": "application/json",
        "Content-Type": "application/json",
    }

    // let token = document.head.querySelector('meta[name="csrf-token"]');
    // if (!!token) {
    //     headers['X-CSRF-TOKEN'] = token.content;
    // }

    return headers
}
