import React from 'react';
import {Field, withFormik} from 'formik';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, Title} from "../../common/form_components";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {CompIcon, CompVariantCheckbox} from "../../common/CompVariant";
import OrderFormInputs from "./Inputs/Inputs_bckp";
import LongCompDefault from "../../../assets/long_icon.svg";
import ShortCompDefault from "../../../assets/short_icon.svg";
import LongCompActive from "../../../assets/long_icon_active.svg";
import ShortCompActive from "../../../assets/short_icon_active.svg";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

const OrderFormWrapper = ({caption="Безымянный", size="xl", children, ...props}) => {
    return (
        <SecondaryLayout>
            <Spacer/>
            <FormWrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption} />
                    {children}
                </InnerFormSpacer>
            </FormWrapper>
        </SecondaryLayout>
    )
}

const CheckBox = ({field, form: {touched, errors}, ...props}) => {
    const {variant} = props

    const labelName = variant === 'long' ? 'Длинный' : 'Короткий'
    const icon = variant === 'long' ? LongCompDefault : ShortCompDefault
    const checkedIcon = variant === 'long' ? LongCompActive : ShortCompActive

    return (
        <div>
            <FormControlLabel
                control={
                    <Checkbox
                        icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                        value={variant} color="default" style={{borderRadius: 0,}}
                        checked={field.value}
                        {...field} {...props}
                    />
                }
                label={labelName}
                labelPlacement="bottom"
                // disabled={disabled}
            />

            {touched[field.name] && errors[field.name] && <div>{errors[field.name]}</div>}
        </div>
    )
};

const TypeVariants = ({handleChange, ...props}) => {
    return (
        <Grid item xs={12}>
            <Box>
                <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                <FormGroup row style={{justifyContent: 'center',}}>
                    <Field
                        variant='long'
                        id="typeVariants.long" name="typeVariants.long" component={CheckBox}
                        onChange={handleChange}
                    />
                    <Field
                        variant='short'
                        id="short" name="short" component={CheckBox}
                        onChange={handleChange}
                    />
                </FormGroup>
            </Box>
        </Grid>
    )
}

const OrderForm = props => {
    const {
        values, touched, errors, orderFormData,
        handleChange, handleBlur, handleSubmit, handleReset
    } = props;

    return (
        <OrderFormWrapper caption="Форма заказа">
            <form onSubmit={handleSubmit}>
                <Grid container>


                    {/*type varient*/}
                    <TypeVariants handleChange={handleChange}/>

                    {/*inputs*/}


                </Grid>
                <button type="submit">Submit</button>
                <button onClick={handleReset}>Reset</button>
            </form>
        </OrderFormWrapper>
    );
};

const getDefaultValues = (state) => {
    let values = {
        typeVariants: {
            long: state.typeVariant === 1,
            short: state.typeVariant === 2,
        }
    }

    // inputs
    Object.keys(state.inputs).forEach(keyName => {
        values[keyName] = ''
    })

    return values
}

const OrderFormContainer = withFormik({
    mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

    // Custom sync validation
    validate: values => {
        const errors = {};

        // if (!values.name) {
        //     errors.name = 'Required';
        // }

        return errors;
    },

    handleSubmit: (values, {setSubmitting, ...props}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
            // props.props.history.push(`${props.props.location.pathname}/success`)
        }, 1000);
    },

    displayName: 'OrderForm',
})(OrderForm);

const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormContainer)
