import React, {useState} from 'react';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import {useStyles} from "./Footer.style";
import Copyright from "./Copyright";


function Footer() {
    const classes = useStyles()
    const phrases = [
        {
            author: 'Руми',
            text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
        },
        {
            author: 'Поль Гоген',
            text: "Я закрываю глаза, чтобы лучше видеть.",
        },
        {
            author: 'Анатоль Франс',
            text: "Чувство меры в искусстве — все.",
        },
        {
            author: 'Альберт Эйнштейн',
            text: "Секрет творчества состоит в умении скрывать источники своего вдохновения",
        },
    ]
    const [currPhrase, setCurrPhrase] = useState({
        ...phrases[Math.floor(phrases.length * Math.random())]
    });

    return (
        <footer className={classes.footer}>
            <Container maxWidth="sm">
                <Box display="flex" justifyContent="center" alignItems="center">
                    <Typography variant="body1" align="center">
                        <q>{currPhrase.text}</q>&nbsp;&ndash;&nbsp;
                        <cite>{currPhrase.author}</cite>.
                    </Typography>
                </Box>
            </Container>
            <Container maxWidth="sm">
                <Box display="flex" justifyContent="center" alignItems="center">
                    <Copyright/>
                </Box>
            </Container>
        </footer>
    )
}

export default Footer
