import React from 'react';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import Typography from "@material-ui/core/Typography";
import {Btn, ButtonsGroup, FormWrapper, InnerFormSpacer} from "../common/form_components";
import CompForm from "./CompForm/CompForm";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Divider from "@material-ui/core/Divider";


function CompFormSummarize(props) {
    return (
        <>
            <Grid container>
                <Grid item xs={12}>
                    <Box mt={2}>
                        <Divider variant="fullWidth" light={false}/>
                    </Box>
                </Grid>
                <Grid item xs={12}>
                    <Box mt={2}>
                        <Typography>
                            Summa
                        </Typography>
                    </Box>
                </Grid>
            </Grid>
        </>
    )
}

function CompFormContainer(props) {
    const [state, setState] = React.useState({
        withBackground: false,
        long: false,
        short: false,
    });
    const [username, setUsername] = React.useState('');


    const handleUsername = event => {
        setUsername(event.target.value);
    };

    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked});
    };

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Typography component="h1" variant="h5" align="center" color="textPrimary">
                        Форма заказа
                    </Typography>

                    <form noValidate onSubmit={(e) => {
                        e.preventDefault();
                        alert('Форма среагировала! Повесь обработчик на кнопку или вызови нужную функцию изнутри обработчика формы')
                    }}>
                        <CompForm inputs={{username, state}} handlers={{handleUsername, handleChange}}/>

                        <CompFormSummarize/>

                        <ButtonsGroup>
                            <Btn variant="outlined">Назад</Btn>
                            <Btn variant="contained">Очистить</Btn>
                            <Btn variant="contained" type="submit" label="Submit">Заказать</Btn>
                        </ButtonsGroup>
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

export default CompFormContainer
