import createMuiTheme from "@material-ui/core/styles/createMuiTheme";

export const ApplyRootTheme = () => {
    return createMuiTheme({
        palette: {
            type: 'dark',
        },
        textField: {
            width: '90%',
            marginLeft: 'auto',
            marginRight: 'auto',
            paddingBottom: 0,
            marginTop: 0,
            fontWeight: 500
        },
        input: {
            color: 'white'
        }
    })
}
