import RCompThumbnail from "../../../assets/img/compositions/static.png";
import RCompPreview from "../../../assets/img/compositions/dynamic.gif";
import * as types from "./types";

export const initialState = {
    isFetching: false,
    compositions: [],
    errors: {}
}

function compositionsReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.CLEAR_LIST:
            return {
                ...state,
                compositions: [],
                errors: {},
            }
        case types.FETCH_LIST:
            return {
                ...state,
                isFetching: true,
                errors: {},
            }
        case types.FETCH_LIST_COMPLETED:
            return {
                ...state,
                isFetching: false,
                compositions: [
                    ...payload.data.map(c => {
                        return {
                            title: c.title,
                            link: c.link_alias,
                            price: c.price,
                            thumbnail: RCompThumbnail,
                            preview: RCompPreview,
                        }
                    })
                ],
                errors: {}
            }
        case types.FETCH_LIST_FAILED:
            return {
                ...state,
                isFetching: false,
                compositions: [],
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        case types.SET_COMPOSITION_FORM:
            return {
                ...state,
                isFetching: true,
                errors: {},
            }
        case types.SET_COMPOSITION_FORM_COMPLETED:
            return {
                ...state,
                isFetching: false,
                errors: {}
            }
        case types.SET_COMPOSITION_FORM_FAILED:
            return {
                ...state,
                isFetching: false,
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        default:
            return state
    }
}

export default compositionsReducer
