import React from 'react';
import {withFormik} from 'formik';
import {connect} from "react-redux";

const OrderForm = props => {
    const {
        values, touched, errors, orderFormData,
        handleChange, handleBlur, handleSubmit,
    } = props;

    return (
        <form onSubmit={handleSubmit}>
            <button type="submit">Submit</button>
        </form>
    );
};

const getDefaultValues = (state) => {

    let values = {
        ...Object.keys(state.inputs).map(keyName => {
            return {
                [keyName]: ''
            }
        })
    }

    console.log(values)
    return {...values}
}

const OrderFormContainer = withFormik({
    mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

    // Custom sync validation
    validate: values => {
        const errors = {};

        if (!values.name) {
            errors.name = 'Required';
        }

        return errors;
    },

    handleSubmit: (values, {setSubmitting}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
        }, 1000);
    },

    displayName: 'OrderForm',
})(OrderForm);

const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormContainer)
