import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Logo from "../_includes/header/Logo";
import Button from "@material-ui/core/Button";
import {Link} from "react-router-dom";

import Drawer from "../_includes/drawer/Drawer";
import Content from "../_includes/content/Content";
import Footer from "../_includes/footer/Footer";

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    toolbar: theme.mixins.toolbar,
    title: {
        flexGrow: 1,
    },
    wrapper: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
    footer: {
        padding: theme.spacing(3, 2),
        marginTop: 'auto',
        backgroundColor:
            theme.palette.type === 'dark' ? theme.palette.grey[800] : theme.palette.grey[200],
    },
}));


export default function Layout(props) {
    const classes = useStyles();

    return (
        <div >


            <AppBar position="fixed" className={classes.appBar} color="default">
                <Toolbar disableGutters style={{padding: '0 4px',}}>
                    <Logo themeColor='dark'/>
                    <Typography variant="h6" className={classes.title}>
                        {props.title}
                    </Typography>

                    <Button component={Link} to="/login" color='inherit'>
                        Login
                    </Button>
                </Toolbar>
            </AppBar>

            <Drawer />

            <div className={classes.wrapper}>
                <Content inner={props.children} />
                <Footer />
            </div>
        </div>
    );
}
