import React from 'react';
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import {ThemeProvider} from '@material-ui/core/styles';
import {connect} from "react-redux";

import {ApplyRootTheme} from "./ApplyRootTheme";
import NotFound from "./components/ErrorPages/NotFound/NotFound";
import Background from "./components/_layouts/default/Background";
import HomeContainer from "./components/Home/HomeContainer";
import CompositionsContainer from "./components/Compositions/CompositionsContainer";
import SuggestPage from "./components/Suggest/SuggestPage";
import ShowcaseContainer from "./components/Showcase/ShowcaseContainer";

const Root = ({themeColor, ...props}) => {
    const theme = ApplyRootTheme(themeColor)

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline/>
            <Router>
                <Background>
                    <Switch>
                        <Route exact path="/" component={HomeContainer}/>
                        <Route exact path="/compositions" component={CompositionsContainer}/>
                        <Route exact path="/suggest" component={SuggestPage}/>
                        <Route path="/compositions/:page" component={CompositionsContainer}/>
                        <Route path="/showcase/:id" component={ShowcaseContainer}/>
                        <Route path="*" component={NotFound}/>
                    </Switch>
                </Background>
            </Router>
        </ThemeProvider>
    );
}

const mapStateToProps = (state) => {
    return {
        themeColor: state.layout.themeColor
    }
}

export default connect(mapStateToProps)(Root)
