    // localStorage["appState"] = JSON.stringify(appState);
    // this.setState(appState);
}

function authLogin(payload) {
    return {
        type: types.AUTH_LOGIN,
        payload
    }
}

function authLogout() {
    return {
        type: types.AUTH_LOGOUT
    }
}
