import {initAppCompleted} from './actions'
// import {me} from "../auth/operations";

const initApp = () => (dispatch) => {
    const promises = []
    const jwt_token = localStorage.getItem('jwt_token')

    if (!!jwt_token) {
        // const mePromise = dispatch(me(jwt_token))
        // promises.push(mePromise)
    }

    Promise.all(promises)
        .then(() => {
            dispatch(initAppCompleted())
        })
}

export {initApp}
