import {
    fetchCompForm,
    clearCompForm,
    makeOrderRequestAC,
} from './actions'

const makeOrderRequest = (form) => (dispatch, getState) => {
    const {alias, errors} = getState().compOrderForm
    console.log(alias)

    const data = {
        alias: alias,
        ...form
    }

    const response = dispatch(makeOrderRequestAC(data))

    if (Object.keys(errors).length) { // promise with error
        return response
    } else { // result
        return Promise.resolve(response);
    }
}

export {
    fetchCompForm,
    clearCompForm,
    makeOrderRequest
}
