import {initAppCompleted} from './actions'
import {getUser} from "../auth/operations";

const initApp = () => (dispatch, getState) => {
    const promises = []
    const authPromise = getUser();

    Promise.all(promises)
    dispatch(initAppCompleted())
}

export {initApp}
