import axios from 'axios'

export const auth = {
    setToken(token) {
        localStorage.setItem('jwt_token', token);
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    },

    getToken(name) {
        localStorage.getItem('name');
    },

    removeToken(name) {
        const token = this.getToken(name)

        if (!!token) {
            localStorage.removeItem(name)
        }
    }
}
