import React, {Component} from 'react';
import {withFormik} from 'formik';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, PlaceholderList, Title, TypeVariants} from "../../common/form_components";
import Grid from "@material-ui/core/Grid";
import OrderFormSummarize from "./Summarize/Summarize";
import OrderFormControls from "./Controls/Controls";
import {compose} from "redux";
import {checkOnMax, checkOnMin, checkOnRequired} from "../../../store/utils/form_helpers";
import {clearCompForm, fetchCompForm} from "../../../store/ducks/comp-order-form/actions";

const OrderFormWrapper = ({caption = "Безымянный", size = "xl", children, ...props}) => {
    return (
        <SecondaryLayout>
            <Spacer/>
            <FormWrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption}/>
                    <form onSubmit={props.handleSubmit} noValidate>
                        {children}
                    </form>
                </InnerFormSpacer>
            </FormWrapper>
        </SecondaryLayout>
    )
}


class OrderForm extends Component {
    componentDidMount() {
        const {fetchCompForm, match} = this.props
        const alias = match.params.alias
        fetchCompForm(alias)
    }

    componentWillUnmount() {
        const {clearCompForm} = this.props
        clearCompForm()
    }

    render() {
        const {
            values, touched, errors, orderFormData: {typeVariant, ph, price, title, isFetching}, handleSubmit, handleReset
        } = this.props;

        return (
            <OrderFormWrapper caption="Форма заказа" handleSubmit={handleSubmit}>
                <Grid container>

                    {/*type varient*/}
                    <TypeVariants
                        variant={typeVariant} values={values}
                        touched={touched} errors={errors}
                        isFetching={isFetching}
                    />
                    {/*inputs*/}
                    {Object.keys(ph).length > 0 && (
                        <PlaceholderList inputs={ph}/>
                    )}

                </Grid>
                <OrderFormSummarize price={price} title={title}/>
                <OrderFormControls onReset={handleReset} onBack={() => {
                    this.props.history.goBack()
                }}/>
            </OrderFormWrapper>
        );
    }
}

const getDefaultValues = ({typeVariant, ph, ...state}) => {
    let values = {
        typeVariant: typeVariant === 0 ? '' : typeVariant === 1 ? "long" : "short"
    }

    // inputs
    Object.keys(ph).forEach(keyName => {
        values[keyName] = ''
    })

    return values
}
const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default compose(
    withFormik({
        mapPropsToValues: (props) => console.log(props),

        // Custom sync validation
        validate: (values, {orderFormData: {ph, typeVariant}, ...props}) => {
            const errors = {};
            const rules = {}

            Object.keys(ph).forEach(keyName => {
                rules[keyName] = ph[keyName].rules
            })

            if (typeVariant === 0 && !values.typeVariant) errors["typeVariant"] = "Required"

            Object.keys(rules).forEach(keyName => {
                if (/required/ig.test(rules[keyName]) && !checkOnRequired(values[keyName])) {
                    errors[keyName] = 'Required'
                }
                if (/min:\d{1,2}/ig.test(rules[keyName])) {
                    let rule = rules[keyName].match(/min:\d{1,2}/ig)[0]
                    let limit = +rule.match(/\d+/ig)[0]
                    if (!checkOnMin(values[keyName], limit)) {
                        errors[keyName] = `Minimum length is ${limit}`
                    }
                }
                if (/max:\d{1,2}/ig.test(rules[keyName])) {
                    let rule = rules[keyName].match(/max:\d{1,2}/ig)[0]
                    let limit = +rule.match(/\d+/ig)[0]
                    if (!checkOnMax(values[keyName], limit)) {
                        errors[keyName] = `Maximum length is ${limit}`
                    }
                }
            })

            return errors;
        },

        handleSubmit: (values, {setSubmitting, ...props}) => {
            setSubmitting(true);
            console.log(JSON.stringify(values, null, 2));
            setSubmitting(false);
            props.props.history.push(`${props.props.location.pathname}/success`)
        },

        enableReinitialize: true,

        displayName: 'OrderForm',
    }),
    connect(mapStateToProps, {fetchCompForm, clearCompForm}),
)(OrderForm)
