import React from 'react'
import {withFormik} from "formik";
import SuggestForm from "./Form";
import {withSnackbar} from "notistack";
import {withRouter} from "react-router-dom";
import {checkOnMax, checkOnMin, checkOnRequired} from "../../../store/utils/form_helpers";

const EnhancedForm = withFormik({
    mapPropsToValues: ({suggestForm: {typeVariant, description}, ...props}) => {
        return {
            typeVariant,
            description
        }
    },

    // Custom sync validation
    validate: (values, {...props}) => {
        const errors = {};

        if (Array.isArray(values.typeVariant) && !values.typeVariant.length) {
            errors["typeVariant"] = "Обязательно к заполнению"
        }

        let minLimit = 10
        let maxLimit = 3072
        if (!checkOnRequired(values.description)) {
            errors["description"] = 'Обязательно к заполнению'
        }
        if (!checkOnMin(values.description, minLimit)) {
            errors["description"] = `Минимальная длина ${minLimit} символов`
        }
        if (!checkOnMax(values.description, maxLimit)) {
            errors["description"] = `Превышена максимальная длина`
        }

        return errors;
    },

    handleSubmit: (values, {setSubmitting, ...props}) => {
        const {makeRequest, enqueueSnackbar, history} = props.props

        const data = {
            ...values
        }

        setSubmitting(true);
        // send...
        makeRequest(data).then((res) => {
            console.log(res)
            setSubmitting(false)
            console.log()
            // history.push('/')

            enqueueSnackbar('good', {
                variant: 'success',
                autoHideDuration: 5000,
            });
        }).catch(err => {
            console.log(err)
            setSubmitting(false)

            enqueueSnackbar('bad', {
                variant: 'error',
                autoHideDuration: 5000,
            });
        })

    },

    enableReinitialize: true,

    displayName: 'SuggestForm',
})(SuggestForm)

export default withSnackbar(withRouter(EnhancedForm))
