import * as types from "./types";


export const initialState = {
    alias: null,
    title: null,
    typeVariant: null,
    price: null,
    ph: {}
    // ph: {
    //     username: {
    //         label: "Никнейм",
    //         rules: 'max:30|min:2',
    //     },
    //     twitch: {
    //         label: "Название канала",
    //         rules: 'min:5',
    //     },
    //     facebook: {
    //         label: "Сторонние ссылки",
    //         rules: '',
    //     },
    // }
}

function compOrderFormReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.SET_COMPOSITION_FORM:
            return {
                ...state,
                isFetching: true,
                errors: {},
            }
        case types.SET_COMPOSITION_FORM_COMPLETED:

            return {
                ...state,
                title: payload.title,
                price: payload.price,
                typeVariant: payload.type_variant,
                isFetching: false,
                errors: {}
            }
        case types.SET_COMPOSITION_FORM_FAILED:
            return {
                ...state,
                isFetching: false,
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        default:
            return state
    }
}


export default compOrderFormReducer
