import React from 'react';
import {withFormik} from 'formik';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, Title} from "../../common/form_components";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {CompVariantCheckbox} from "../../common/CompVariant";

function TypeVariants(props) {
    const {typeVariant} = props.inputs
    const {handleTypeVariant} = props.handlers

    return (
        <Grid container>
            <Grid item xs={12}>
                <Box>
                    <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                    <FormGroup row style={{justifyContent: 'center',}}>
                        <CompVariantCheckbox variant='long' handler={handleTypeVariant} checked={typeVariant.long.value}
                                             disabled={typeVariant.long.disabled}/>
                        <CompVariantCheckbox variant='short' handler={handleTypeVariant}
                                             checked={typeVariant.short.value} disabled={typeVariant.short.disabled}/>
                    </FormGroup>
                </Box>
            </Grid>
        </Grid>
    )
}


const OrderForm = props => {
    const {
        values, touched, errors, orderFormData,
        handleChange, handleBlur, handleSubmit, handleReset
    } = props;

    return (
        <SecondaryLayout>
            <Spacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Форма заказа"/>


                    <form onSubmit={handleSubmit}>
                        {/*type varient*/}
                        <TypeVariants />

                        {/*inputs*/}

                        <button type="submit">Submit</button>
                        <button onClick={handleReset}>Reset</button>
                    </form>


                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
};

const getDefaultValues = (state) => {
    let values = {
        typeVariant: {
            long: state.typeVariant === 1,
            short: state.typeVariant === 2,
        }
    }

    // inputs
    Object.keys(state.inputs).forEach(keyName => {
        values[keyName] = ''
    })

    return values
}

const OrderFormContainer = withFormik({
    mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

    // Custom sync validation
    validate: values => {
        const errors = {};

        // if (!values.name) {
        //     errors.name = 'Required';
        // }

        return errors;
    },

    handleSubmit: (values, {setSubmitting, ...props}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
            // props.props.history.push(`${props.props.location.pathname}/success`)
        }, 1000);
    },

    displayName: 'OrderForm',
})(OrderForm);

const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormContainer)
