import React from 'react';
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import Typography from "@material-ui/core/Typography";
import {withRouter} from "react-router-dom";

function NavProfile(props) {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <>
            <Typography>
                balance
            </Typography>
            <IconButton aria-label="account of current user" aria-controls="menu-appbar"
                        aria-haspopup="true" onClick={handleMenu} color="inherit"
            >
                <Avatar alt={props.username} src={props.avatar} style={{width: 32, height: 32,}} />
            </IconButton>
            <Menu
                id="menu-appbar" anchorEl={anchorEl} keepMounted elevation={0} getContentAnchorEl={null}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'right',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                }}
                open={open} onClose={handleClose} style={{top: '5px'}}
            >
                {/*+(заказы, статистика, настройки)*/}
                <MenuItem onClick={() => {
                    handleClose()
                    props.logout().then(flag => {
                        if (flag === true) props.history.push("/") // steam auth
                    })
                }}>Выйти</MenuItem>
            </Menu>
        </>
    );
}

export default withRouter(NavProfile)
