import {
    authLogin,
    authLogout,
    authLoginCompleted,
    authLogoutCompleted,
    authLoginFailed,
} from "./actions";
import {fetch} from "../../utils";
import {handleHTTPError} from "../error/operations";
import UserApi from "../../utils/UserApi";
import history from "../../utils/history";

const login = (params) => (dispatch) => {
    dispatch(authLogin())
    return fetch(`/airlock/csrf-cookie`, "GET")
        .then(() => {
            fetch(`/api/login/steam/callback${params}`, "GET")
                .then((user) => {
                    UserApi.authenticate(() => dispatch(authLoginCompleted(user)))
                })
                .catch((err) => {
                    dispatch(authLoginFailed())
                    dispatch(handleHTTPError(err))
                });
        })
        .catch(err => {
            dispatch(authLoginFailed())
            dispatch(handleHTTPError(err))
        });
}

const logout = () => dispatch => {
    dispatch(authLogout())
    return fetch(`/api/logout`, "POST")
        .then((res) => {
            UserApi.logout(() => {
                dispatch(authLogoutCompleted())
                // history.push('/')
            })
        })
        .catch(err => {
            dispatch(handleHTTPError(err))
        });
}

const me = () => dispatch => {
    dispatch(authLogin())
    return fetch(`/api/user`, "GET")
        .then((user) => {
            UserApi.authenticate(() => dispatch(authLoginCompleted(user)))
            return user
        })
        .catch((err) => {
            // dispatch(logout())
            dispatch(handleHTTPError(err))
        });
}


export {
    login, logout, me
}
