import React from 'react';
import {connect} from "react-redux";
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import {FormWrapper, InnerFormSpacer, Title} from "../common/form_components";
import CompFormInputs from "./CompFormInputs/CompFormInputs";
import CompFormSummarize from "./CompFormSummarize/CompFormSummarize";
import CompFormControls from "./CompFormControls/CompFormControls";


function CompFormContainer(props) {
    const {balance} = props.user
    const compMeta = props.compData

    const getDefaultState = () => {
        let inputs = {...compMeta.inputs}
        if (Object.entries(inputs).length > 0) {
            inputs = Object.keys(inputs).map(obj => {
                return {
                    [obj]: ''
                }
            })
        }


        console.log(compMeta.inputs)
        console.log('==========================')
        console.log(inputs)

        return ({
            typeVariant: {
                long: false,
                short: false,
            },
            inputs: inputs,
        })
    }

    const [state, setState] = React.useState(getDefaultState);

    const handleTypeVariant = name => event => {
        setState({
            ...state,
            typeVariant: {
                ...state.typeVariant,
                [name]: event.target.checked,
            }
        });
    };

    const handleInputs = event => {
        const inpName = event.target.id
        const inpValue = event.target.value

        setState({
            ...state,
            inputs: {
                ...state.inputs,
                [inpName]: {
                    ...state.inputs[inpName],
                    value: inpValue
                }
            }
        })

    }

    const onClear = e => {
        setState(getDefaultState())
        // clear session / localStorage (action)
    }
    const onBack = e => {
        props.history.goBack()
    }
    const onSubmit = e => {
        e.preventDefault();
        alert('ОК! Повесь обработчик на кнопку или вызови нужную функцию изнутри обработчика формы')
    }

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Форма заказа"/>

                    <form noValidate onSubmit={onSubmit}>
                        <CompFormInputs inputs={state} handlers={{handleTypeVariant, handleInputs}}/>
                        <CompFormSummarize balance={balance} price={compMeta.price} title={compMeta.title}/>
                        <CompFormControls onClear={onClear} onBack={onBack}/>
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

const mapStateToProps = (state) => ({
    user: state.user,
    compData: state.orderFormPage
})
const mapDispatchToProps = (dispatch) => ({})

export default connect(mapStateToProps, mapDispatchToProps)(CompFormContainer)
