import React from 'react'
import Checkbox from "@material-ui/core/Checkbox";
import Radio from "@material-ui/core/Radio";

import FormControlLabel from "@material-ui/core/FormControlLabel";
import Icon from "@material-ui/core/Icon";

import LongCompDefault from '../../assets/long_icon.svg'
import LongCompActive from '../../assets/long_icon_active.svg'
import ShortCompDefault from '../../assets/short_icon.svg'
import ShortCompActive from '../../assets/short_icon_active.svg'
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {Field} from "formik";


export function CompIcon(props) {
    return (
        <Icon style={{textAlign: 'center', fontSize: '7.5rem',}}>
            <img {...props} height="100%" width="100%" alt="Composition Icon"/>
        </Icon>
    );
}

export const CompVariantCB = ({field, form: {touched, errors}, variant, ...props}) => {
    const labelName = variant === 'long' ? 'Длинный' : 'Короткий'
    const icon = variant === 'long' ? LongCompDefault : ShortCompDefault
    const checkedIcon = variant === 'long' ? LongCompActive : ShortCompActive

    return (
        <div>
            <FormControlLabel
                control={
                    <Checkbox
                        icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                        value={variant} color="default" style={{borderRadius: 0,}}
                        checked={field.value}
                        {...field} {...props}
                    />
                }
                label={labelName}
                labelPlacement="bottom"
                // disabled={disabled}
            />

            {touched[field.name] && errors[field.name] && <div>{errors[field.name]}</div>}
        </div>
    )
}

export const CompVariantRB = ({
                                  field,
                                  id, label, checked, disabled, ...props
                              }) => {
    const icon = id === 'long' ? LongCompDefault : ShortCompDefault
    const checkedIcon = id === 'long' ? LongCompActive : ShortCompActive

    return (
        <div>
            <FormControlLabel
                control={
                    <Radio
                        icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                        color="default" style={{borderRadius: 0,}}
                        id={id} name={field.name}
                        value={id} checked={checked}
                        onChange={field.onChange} onBlur={field.onBlur}
                        {...props}
                    />
                }
                label={label}
                labelPlacement="bottom"
                disabled={disabled}
            />
        </div>
    );
};


export const TypeVariants = ({variant, values, ...props}) => {
    return (
        <Grid item xs={12}>
            <Box>
                <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                <FormGroup row style={{justifyContent: 'center',}}>
                    <Field
                        component={CompVariantRB} name="typeVariant"
                        id="long" label="Длинный"
                        checked={variant === 0 ? "long" === values.typeVariant : variant === 1}
                        disabled={variant === 2}
                        onChange={handleChange} onBlur={handleBlur}
                    />
                    <Field
                        component={CompVariantRB} name="typeVariant"
                        id="short" label="Короткий"
                        checked={variant === 0 ? "short" === values.typeVariant : variant === 2}
                        disabled={variant === 1}
                        onChange={handleChange} onBlur={handleBlur}
                    />
                </FormGroup>
            </Box>
        </Grid>
    )
}
