import React from 'react'
import styled from "styled-components";

const Content = styled.div`
    position: relative;
    padding: 0 12px 64px 12px;
    width: 926px;
    margin: 0 auto;
    font-size: 13px;
    background: rgba(23, 23, 23, 0.85);
    margin-top: -16px;
    overflow: hidden;
    padding-top: 16px;
`

const ProfileContentInner = styled.div`
    position: relative;
`

const ProfileRightCol = styled.div`
    width: 268px;
    float: right;
`

const ProfileLeftCol = styled.div`
    width: 636px;
    float: left;
`

const ProfileCustomizationArea = styled.div`
`

const ProfileCustomization = styled.div`
    position: relative;
    background-image: url('https://steamcommunity-a.akamaihd.net/public/images/profile/showcase_bg.png');
    background-position: top left;
    background-repeat: no-repeat;
    background-color: #141414;
    border-radius: 3px;
    padding: 0 10px 11px 10px;
    margin-bottom: 12px;
    font-size: 13px;
    overflow: hidden;
`

const ProfileCustomizationHeader = styled.div`
    font-weight: 200;
    color: #5491cf;
    font-size: 20px;
    line-height: 30px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
`

const ProfileCustomizationBlock = styled.div`
`

const ScreenshotShowcase = styled.div`
`

const ScreenshotShowcasePrimary = styled.div`
    display: block;
    float: left;
    position: relative;
    width: 508px;
    max-width: 83%;
`

const ScreenshotShowcaseRightCol = styled.div`
    float: right;
    max-width: 17%;
    width: 102px;
    text-align: center;
`

const ScreenshotShowcasePrimaryLink = styled.span`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    margin-bottom: 4px;
    
    &:hover {
        border: 1px solid #97C0E3;    
    }
`

const ScreenshotShowcasePrimaryImage = styled.img`
    display: block;
    max-width: 506px;
`

const ScreenshotShowcaseSmall = styled.div`
    display: block;
    float: left;
    position: relative;
    float: none;
    margin-bottom: 11px;
`

const ScreenshotShowcaseSmallLink = styled.span`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    
    &:hover {
        border: 1px solid #97C0E3;    
    }
`

const ScreenshotShowcaseSmallImage = styled.img`
    display: block;
    max-width: 100px;
`


const ProfileContent = (props) => {
    return (
        <Content>
            <ProfileContentInner>
                <ProfileRightCol/>
                <ProfileLeftCol>
                    <ProfileCustomizationArea>
                        <ProfileCustomization>
                            <ProfileCustomizationHeader>Витрина иллюстраций</ProfileCustomizationHeader>

                            <ProfileCustomizationBlock>
                                <ScreenshotShowcase>
                                    <ScreenshotShowcasePrimary>
                                        <ScreenshotShowcasePrimaryLink>
                                            <ScreenshotShowcasePrimaryImage width="100%"
                                                                            src="https://steamuserimages-a.akamaihd.net/ugc/785249992931904478/33459A20BFFFF35CAF4B33E27C843663F6AA3FA5/?imw=506&&ima=fit&impolicy=Letterbox&imcolor=%23000000&letterbox=false"/>
                                        </ScreenshotShowcasePrimaryLink>
                                    </ScreenshotShowcasePrimary>
                                    <ScreenshotShowcaseRightCol>
                                        <ScreenshotShowcaseSmall>
                                            <ScreenshotShowcaseSmallLink>
                                                <ScreenshotShowcaseSmallImage width="100%"
                                                                              src="https://steamuserimages-a.akamaihd.net/ugc/785249992931902062/6DD560C2E2299F3C1A5F0081009FA40BAB6DD63D/?imw=100&&ima=fit&impolicy=Letterbox&imcolor=%23000000&letterbox=false"/>
                                            </ScreenshotShowcaseSmallLink>
                                        </ScreenshotShowcaseSmall>
                                    </ScreenshotShowcaseRightCol>
                                    <div style={{clear: 'both',}}/>
                                </ScreenshotShowcase>
                            </ProfileCustomizationBlock>

                        </ProfileCustomization>
                    </ProfileCustomizationArea>
                </ProfileLeftCol>
                <div style={{clear: 'both',}}/>
            </ProfileContentInner>
        </Content>
    )
}

export default ProfileContent
