import {authCheck, authLogout, authLogin} from "./actions";
import Http from '../../utils/Http'

const login = (response) => {

    return dispatch => (
        new Promise((resolve, reject) => {
            if (response.success) {
                const {data: creds} = response
                dispatch(authLogin(creds));
                return resolve();
            } else {
                return reject();
            }
        })
    )

}

const logout = () => {
    return dispatch => (
        dispatch(authLogout())
    )
}


export {
    login, logout
}
