import React from 'react';
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import {withRouter} from "react-router-dom";
import {connect} from "react-redux";
import {logout} from "../../../store/ducks/auth/operations";

function NavProfile({logout, ...props}) {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <>
            <IconButton aria-label="account of current user" aria-controls="menu-appbar"
                        aria-haspopup="true" onClick={handleMenu} color="inherit"
            >
                <Avatar alt={props.nickname}
                        src={props.avatar}
                        style={{width: 32, height: 32,}}
                />
            </IconButton>
            <Menu
                id="menu-appbar" anchorEl={anchorEl} keepMounted elevation={0} getContentAnchorEl={null}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'right',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                }}
                open={open} onClose={handleClose} style={{top: '5px'}}
            >
                {/*+(заказы, статистика, настройки)*/}
                <MenuItem onClick={() => {
                    handleClose()
                    logout()
                }}>Logout</MenuItem>
            </Menu>
        </>
    );
}

export default connect(null, {logout})(withRouter(NavProfile))
