import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import {Link} from "react-router-dom";
import {makeStyles} from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import RAvatar from "../../../../../assets/img/my_avatar.jpg"

import Logo from "./Logo";

const useStyles = makeStyles(theme => ({
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
    },
    title: {
        flexGrow: 1,
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3)
    },
    medium: {
        width: theme.spacing(4),
        height: theme.spacing(4)
    },
    large: {
        width: theme.spacing(7),
        height: theme.spacing(7)
    },
}));

export default function Header(props) {
    const classes = useStyles();
    const [auth, setAuth] = React.useState(false);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const handleChange = event => {
        setAuth(event.target.checked);
    };

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };


    return (
        <AppBar position="fixed" className={classes.appBar} color="default">
            <Toolbar disableGutters style={{padding: '0 4px',}}>
                <Logo themeColor='dark' size={classes.medium}/>
                <Typography variant="h6" className={classes.title}>
                    {props.title}
                </Typography>
                {auth ? (
                        <>
                            <IconButton
                                aria-label="account of current user"
                                aria-controls="menu-appbar"
                                aria-haspopup="true"
                                onClick={handleMenu}
                                color="inherit"
                            >
                                <Avatar alt="dlinka_" src={RAvatar} className={classes.medium}/>
                            </IconButton>
                            <Menu
                                id="menu-appbar"
                                anchorEl={anchorEl}
                                keepMounted
                                elevation={0}
                                getContentAnchorEl={null}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'right',
                                }}
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right',
                                }}
                                open={open}
                                onClose={handleClose}
                                style={{top: '7px'}}
                            >
                                <MenuItem onClick={handleClose}>Orders</MenuItem>
                                <MenuItem onClick={handleClose}>Stats</MenuItem>
                                <MenuItem onClick={handleClose}>Settings</MenuItem>
                                <MenuItem onClick={handleClose}>Logout</MenuItem>
                            </Menu>
                        </>
                    )
                    :
                    <Button component={Link} to="/login" color='inherit'>
                        Войти
                    </Button>
                }
            </Toolbar>
        </AppBar>
    );
}
