import React from 'react'
import styled from "styled-components";
import RLongPrimaryImage from "../../../assets/img/showcase/long_primary.gif"
import RLongSecondary from "../../../assets/img/showcase/long_secondary.jpg"

import RShortPrimary from "../../../assets/img/showcase/short_primary.jpg"
import RShortSecondaryOne from "../../../assets/img/showcase/short_secondary1.jpg"
import RShortSecondaryTwo from "../../../assets/img/showcase/short_secondary2.jpg"
import RShortSecondaryTree from "../../../assets/img/showcase/short_secondary3.jpg"


const ContentWrapper = styled.div`
    position: relative;
    padding: 0 12px 64px 12px;
    width: 926px;
    margin: 0 auto;
    font-size: 13px;
    background: rgba(23, 23, 23, 0.85);
    margin-top: -16px;
    overflow: hidden;
    padding-top: 16px;
`

const Content = styled.div`
    position: relative;
`

const RightCol = styled.div`
    width: 268px;
    float: right;
`

const LeftCol = styled.div`
    width: 636px;
    float: left;
`

const CustomizationArea = styled.div`
`

const Customization = styled.div`
    position: relative;
    background-image: url('https://steamcommunity-a.akamaihd.net/public/images/profile/showcase_bg.png');
    background-position: top left;
    background-repeat: no-repeat;
    background-color: #141414;
    border-radius: 3px;
    padding: 0 10px 11px 10px;
    margin-bottom: 12px;
    font-size: 13px;
    overflow: hidden;
`

const CustomizationHeader = styled.div`
    font-weight: 200;
    color: #5491cf;
    font-size: 20px;
    line-height: 30px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
`

const CustomizationBlock = styled.div`
`

const ShowcaseWrapper = styled.div`
`

const SlotWrapper = styled.div`
    float: left;
    position: relative;
    display: block;
`

const Primary = styled(SlotWrapper)`
    max-width: 83%;
    width: 508px;
`

const Secondary = styled.div`
    float: right;
    max-width: 17%;
    width: 102px;
    text-align: center;
`

const SlotsWrapper = styled(SlotWrapper)`
    margin-bottom: 11px;
    float: none;
`

const SlotLink = styled.span`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    
    &:hover {
        border: 1px solid #97C0E3;    
    }
    
    ${({primary}) => primary && `margin-bottom: 4px;`}
`

const SlotImage = styled.img`
    display: block;
    max-width: ${({type}) => type === "secondary" ? "100px" : "506px"};
    width: 100%;
`

const Showcase = ({images, ...props}) => {

    return (
        <Customization>
            <CustomizationHeader>Витрина иллюстраций</CustomizationHeader>
            <CustomizationBlock>
                <ShowcaseWrapper>
                    <Primary>
                        <SlotLink primary>
                            <SlotImage
                                src={images.primary}/>
                        </SlotLink>
                    </Primary>
                    <Secondary>
                        {images.secondaries.map((s, index) => (
                            <SlotsWrapper key={index}>
                                <SlotLink>
                                    <SlotImage type="secondary"
                                               src={s}/>
                                </SlotLink>
                            </SlotsWrapper>
                        ))}
                    </Secondary>
                    <div style={{clear: 'both',}}/>
                </ShowcaseWrapper>
            </CustomizationBlock>
        </Customization>
    )
}

const ProfileContent = (props) => {
    const long = {
        primary: RLongPrimaryImage,
        secondaries: [RLongSecondary],
    }
    const short = {
        primary: RShortPrimary,
        secondaries: [RShortSecondaryOne, RShortSecondaryTwo, RShortSecondaryTree,],
    }
    return (
        <ContentWrapper>
            <Content>
                <RightCol/>
                <LeftCol>
                    <CustomizationArea>

                        {/*<Showcase images={long}/>*/}
                        <Showcase images={short}/>

                    </CustomizationArea>
                </LeftCol>
                <div style={{clear: 'both',}}/>
            </Content>
        </ContentWrapper>
    )
}

export default ProfileContent
