import React from 'react';
import Grid from "@material-ui/core/Grid";
import FormGroup from "@material-ui/core/FormGroup";
import {CompVariantCheckbox} from "../../common/CompVariant";
import FormLabel from "@material-ui/core/FormLabel";
import Box from "@material-ui/core/Box";
import TextField from "@material-ui/core/TextField";

function PlaceholderList({inputs, handler, ...props}) {
    return Object.entries(inputs).map(([inputName, inputInfo], id) => (
        <Grid key={id} item xs={12}>
            <Box mt={2}>
                <TextField
                    required id={inputName} label={inputInfo.label} variant="standard"
                    fullWidth value={inputInfo.value} onChange={handler}
                    InputProps={{
                        style: {
                            color: "yellow",
                        }
                    }}
                    InputLabelProps={{
                        style: {
                            color: "red",
                            backgroundColor: "yellow",
                        }
                    }}
                    floatingLabelFocusStyle={{style: {
                            color: "red",
                            backgroundColor: "yellow",
                        }}}
                />
            </Box>
        </Grid>
    ))
}

function OrderFormInputs(props) {
    const {typeVariant, inputs,} = props.inputs
    const {handleTypeVariant, handleInputs} = props.handlers

    return (
        <>
            <Grid container>
                <Grid item xs={12}>
                    <Box>
                        <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                        <FormGroup row style={{justifyContent: 'center',}}>
                            <CompVariantCheckbox variant='long' handler={handleTypeVariant} checked={typeVariant.long.value} disabled={typeVariant.long.disabled} />
                            <CompVariantCheckbox variant='short' handler={handleTypeVariant}
                                                 checked={typeVariant.short.value} disabled={typeVariant.short.disabled} />
                        </FormGroup>
                    </Box>
                </Grid>

                {Object.entries(inputs).length > 0 && (<PlaceholderList inputs={inputs} handler={handleInputs}/>)}
            </Grid>
        </>
    )
}


export default OrderFormInputs
