import React from 'react';
import {Field, withFormik} from 'formik';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, TextInput, Title} from "../../common/form_components";
import Grid from "@material-ui/core/Grid";
import {TypeVariants} from "../../common/CompVariant";
import Box from "@material-ui/core/Box";
import OrderFormSummarize from "./Summarize/Summarize";
import OrderFormControls from "./Controls/Controls";

const OrderFormWrapper = ({caption = "Безымянный", size = "xl", children, ...props}) => {
    return (
        <SecondaryLayout>
            <Spacer/>
            <FormWrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption}/>
                    <form onSubmit={props.handleSubmit} noValidate>
                        {children}
                    </form>
                </InnerFormSpacer>
            </FormWrapper>
        </SecondaryLayout>
    )
}

const Placeholder = ({field, form, ...props}) => {
    return (
        <Box mt={2}>
            <TextInput
                required variant="outlined" fullWidth
                id={inputName} label={inputInfo.label} value={inputInfo.value} onChange={() => {
            }}
                {...field} {...props}
            />
        </Box>
    )
};

function PlaceholderList({inputs, ...props}) {

    return Object.entries(inputs).map(([inputName, inputInfo], id) => (
        <Grid key={id} item xs={12}>
            <Field name="lastName" placeholder="Doe" component={Placeholder}/>
        </Grid>
    ))
}

const OrderForm = props => {
    const {
        values, touched, errors, orderFormData: {typeVariant, ph, price, title},
        handleChange, handleBlur, handleSubmit, handleReset
    } = props;

    return (
        <OrderFormWrapper caption="Форма заказа" handleSubmit={handleSubmit}>
            <Grid container>

                {/*type varient*/}
                <TypeVariants
                    variant={typeVariant}
                    values={values}
                    handleChange={handleChange} handleBlur={handleBlur}
                />
                {/*inputs*/}

                {Object.keys(ph).length > 0 && (
                    <PlaceholderList inputs={ph}/>
                )}

            </Grid>
            <OrderFormSummarize price={price} title={title}/>
            <OrderFormControls onReset={handleReset} onBack={() => {
            }}/>
        </OrderFormWrapper>
    );
};

const OrderFormContainer = withFormik({
    mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

    // Custom sync validation
    validate: values => {
        const errors = {};

        // if (!values.name) {
        //     errors.name = 'Required';
        // }

        return errors;
    },

    handleSubmit: (values, {setSubmitting, ...props}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
            // props.props.history.push(`${props.props.location.pathname}/success`)
        }, 1000);
    },

    displayName: 'OrderForm',
})(OrderForm);

function getDefaultValues({typeVariant, ph, ...state}) {
    let values = {
        typeVariant: typeVariant === 0 ? '' : typeVariant === 1 ? "long" : "short"
    }

    // inputs
    Object.keys(ph).forEach(keyName => {
        values[keyName] = ''
    })

    return values
}

const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormContainer)
