import React from 'react';
import { withFormik } from 'formik';
import {connect} from "react-redux";

const OrderForm = props => {
    const {
        values, touched, errors, orderFormData,
        handleChange, handleBlur, handleSubmit,
    } = props;

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.name}
                name="name"
            />
            {errors.name && touched.name && <div id="feedback">{errors.name}</div>}
            <button type="submit">Submit</button>
        </form>
    );
};

const getDefaultState = (state) => {
    return state
}

const OrderFormContainer = withFormik({
    mapPropsToValues: (props) => (getDefaultState(props.orderFormData)),

    // Custom sync validation
    validate: values => {
        const errors = {};

        if (!values.name) {
            errors.name = 'Required';
        }

        return errors;
    },

    handleSubmit: (values, { setSubmitting }) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
        }, 1000);
    },

    displayName: 'OrderForm',
})(OrderForm);

const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormContainer)
