import React from 'react';
import {useStyles} from "./Home.style";
import {withTheme} from '@material-ui/core/styles';
import {Link} from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import ButtonBase from "@material-ui/core/ButtonBase";
import Box from "@material-ui/core/Box";

import Header from "../../components/_layouts/_includes/header/Header";
import Content from "../../components/_layouts/_includes/content/Content";
import Footer from "../../components/_layouts/_includes/footer/Footer";

import RSteamProfile from '../../assets/img/aw_steam_profile.png'
import RAeWorkflow from '../../assets/img/aw_ae_workflow.png'

const images = [
    {
        url: RAeWorkflow,
        title: 'Предложить идею',
        rotate: '2',
        to: '/suggest',
    },
    {
        url: RSteamProfile,
        title: 'Посмотреть композиции',
        width: '50%',
        rotate: '-5',
        to: '/compositions',
    },
];


function HomePage(props) {
    const classes = useStyles();
    const theme = props.theme

    return (
        <div className={classes.root} style={{background: theme.palette.primary.mainGradient}}>
            {/* eslint-disable-next-line react/jsx-no-undef */}
            <Header/>
            <Content title="Предложи свою идею для создания композиции или зацени уже имеющиеся!">
                <Box display="flex" justifyContent="center" alignItems="center" m={1} p={1}>
                    {images.map(image => {
                        return (
                            <Box key={image.title} m={5}>
                                <ButtonBase
                                    component={Link} to={image.to}
                                    focusRipple
                                    className={classes.image}
                                    focusVisibleClassName={classes.focusVisible}
                                    p={1}
                                    style={{transform: `rotate(${image.rotate}deg)`}}>
                                          <span
                                              className={classes.imageSrc}
                                              style={{
                                                  backgroundImage: `url(${image.url})`,
                                              }}
                                          />
                                    <span className={classes.imageBackdrop}/>
                                    <span className={classes.imageButton}>
                                            <Typography
                                                component="span"
                                                variant="subtitle1"
                                                color="inherit"
                                                className={classes.imageTitle}
                                            >{image.title}</Typography>
                                        </span>
                                </ButtonBase>
                            </Box>
                        )
                    })}
                </Box>
            </Content>
            <Footer/>
        </div>
    );
}

export default withTheme(HomePage)
