import {combineReducers} from "redux";
import * as types from "./types";
import RCompThumbnail from "../../../assets/img/compositions/static.png";
import RCompPreview from "../../../assets/img/compositions/dynamic.gif";

export const initCompList = {
    compositions: [],
    isFetching: false,
    errors: {}
}

function compListReducer(state = initCompList, {type, payload = null}) {
    switch (type) {
        case types.CLEAR_LIST:
            return {
                ...state,
                compositions: [],
                form: {},
                errors: {},
            }
        case types.FETCH_LIST:
            return {
                ...state,
                form: {},
                isFetching: true,
                errors: {},
            }
        case types.FETCH_LIST_COMPLETED:
            return {
                ...state,
                compositions: [
                    ...payload.data.map(c => {
                        return {
                            title: c.title,
                            link: c.link_alias,
                            price: c.price,
                            thumbnail: RCompThumbnail,
                            preview: RCompPreview,
                        }
                    })
                ],
                form: {},
                isFetching: false,
                errors: {}
            }
        case types.FETCH_LIST_FAILED:
            return {
                ...state,
                isFetching: false,
                compositions: [],
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        default:
            return state
    }
}

export const initCompOrderForm = {
    form: {},
    isFetching: false,
    errors: {}
}

function compOrderFormReducer(state = initCompOrderForm, {type, payload = null}) {
    switch (type) {
        case types.SET_COMPOSITION_FORM:
            return {
                ...state,
                form: {},
                isFetching: true,
                errors: {},
            }
        case types.SET_COMPOSITION_FORM_COMPLETED:
            return {
                ...state,
                form: {},
                isFetching: false,
                errors: {}
            }
        case types.SET_COMPOSITION_FORM_FAILED:
            return {
                ...state,
                form: {},
                isFetching: false,
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        default:
            return state
    }
}

export default combineReducers({
    compListReducer,
    compOrderFormReducer
})
