import React from 'react';
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import ButtonBase from "@material-ui/core/ButtonBase";
import Box from "@material-ui/core/Box";

import Header from "../_layouts/_includes/header/Header";
import Content from "../_layouts/_includes/content/Content";
import Footer from "../_layouts/_includes/footer/Footer";

import RSteamProfile from '../../../assets/img/aw_steam_profile.png'
import RAeWorkflow from '../../../assets/img/aw_ae_workflow.png'
import {Link} from "react-router-dom";

const images = [
    {
        url: RAeWorkflow,
        title: 'Предложить идею',
        width: '500px',
        rotate: '2',
        to: '/suggest',
    },
    {
        url: RSteamProfile,
        title: 'Посмотреть композиции',
        width: '500px',
        rotate: '-5',
        to: '/compositions',
    },
];


const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
    wrapper: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },

    image: {
        position: 'relative',
        height: 400,
        boxShadow: '0 2px 2px 1px rgba(0, 0, 0, .95)',
        transition: 'all .15s ease-in-out',
        [theme.breakpoints.down('xs')]: {
            width: '100% !important', // Overrides inline-style
            height: 100,
        },
        '&:hover, &$focusVisible': {
            boxShadow: '0 5px 8px 2px rgba(0, 0, 0, .95)',
            zIndex: 1,
            '& $imageBackdrop': {
                opacity: 0.15,
            },
            '& $imageTitle': {
                border: '4px solid currentColor',
            },
        },
        '&:active': {
            boxShadow: '0 5px 8px 2px rgba(243, 156, 18, .95)',
            transition: 'all .75s ease-in-out',
        },
    },
    focusVisible: {},
    imageButton: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: theme.palette.common.white,
    },
    imageSrc: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        backgroundSize: 'cover',
        backgroundPosition: 'center 40%',
    },
    imageBackdrop: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        backgroundColor: theme.palette.common.black,
        opacity: 0.4,
        transition: theme.transitions.create('opacity'),
    },
    imageTitle: {
        position: 'relative',
        padding: `${theme.spacing(2)}px ${theme.spacing(4)}px ${theme.spacing(1) + 6}px`,
        '&:active': {
            color: 'rgba(243, 156, 18, .95)',
            transition: 'all .75s ease-in-out',
        },
    },
}));

function Home(props) {
    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Header/>
            <Content title="Предложи свою идею для создания композиции или зацени уже имеющиеся!">
                <Box display="flex" justifyContent="center" alignItems="center" m={1} p={1}>
                    {images.map(image => {
                        return (
                            <Box key={image.title} m={5}>
                                <ButtonBase
                                    component={Link} to={image.to}
                                    focusRipple
                                    className={classes.image}
                                    focusVisibleClassName={classes.focusVisible}
                                    p={1}
                                    style={{width: image.width, transform: `rotate(${image.rotate}deg)`}}>
                                          <span
                                              className={classes.imageSrc}
                                              style={{
                                                  backgroundImage: `url(${image.url})`,
                                              }}
                                          />
                                    <span className={classes.imageBackdrop}/>
                                    <span className={classes.imageButton}>
                                            <Typography
                                                component="span"
                                                variant="subtitle1"
                                                color="inherit"
                                                className={classes.imageTitle}
                                            >{image.title}</Typography>
                                        </span>
                                </ButtonBase>
                            </Box>
                        )
                    })}
                </Box>
            </Content>
            <Footer/>
        </div>
    );
}

export default Home
