import {makeStyles} from "@material-ui/core/styles"

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
        backgroundColor: '#ddd',
        margin: '0 auto 25px',
    },
    control: {
        padding: theme.spacing(2),
    },


    // pagination
    pagination: {
        position: 'relative',
        textAlign: 'center',
        padding: '20px 0',
        fontSize: 0,
        zIndex: 6,
        margin: '50px 0',
    },
    // pageClassName
    item: {
        fontSize: 14,
        textDecoration: 'none',
        transition: 'background 250ms',
        // display: 'inline-block',
        textTransform: 'uppercase',
        margin: '0 3px 6px',
        height: 38,
        minWidth: 38,
        borderRadius: 38,
        border: '2px solid #fff',
        lineHeight: 38,
        padding: 0,
        color: '#fff',
        fontWeight: 700,
        letterSpacing: '.03em',
        display: 'none',

        '&:first-child, &:last-child': {
            padding: '0 16px',
            margin: '0 12px 6px',
        },
        '&:last-child, &:nth-child(2), &:nth-last-child(2)': {
            display: 'inline-block',
        },
        '&:hover': {
            backgroundColor: '#000',
            color: '#fff',
        },
    },
}))

export default useStyles
