import React from 'react';
import {connect} from "react-redux";
import Button from "@material-ui/core/Button";

class LoginButton extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            steamLoginUrl: "https://steamcommunity.com/openid/login?openid.ns=http%3A%2F%2Fspecs.openid.net%2Fauth%2F2.0&openid.mode=checkid_setup&openid.return_to=http%3A%2F%2Flocalhost%3A3000%2Fauth%2Fsteam&openid.realm=http%3A%2F%2Flocalhost%3A3000&openid.ns.sreg=http%3A%2F%2Fopenid.net%2Fextensions%2Fsreg%2F1.1&openid.claimed_id=http%3A%2F%2Fspecs.openid.net%2Fauth%2F2.0%2Fidentifier_select&openid.identity=http%3A%2F%2Fspecs.openid.net%2Fauth%2F2.0%2Fidentifier_select"
        }
    }

    // componentDidMount() {
    //     axios.get('/api/login/steam')
    //         .then((data) => {
    //             this.setState({
    //                 steamLoginUrl: data.url
    //             })
    //         })
    //         .catch((error) => this.setState({
    //             steamLoginUrl: error
    //         }));
    // }

    render() {
        return (
            <>
                {this.state.steamLoginUrl && (
                    <Button href={this.state.steamLoginUrl} color="inherit">Войти</Button>
                )}
            </>
        )

    }
}


export default LoginButton
